var R0=0;var R1=1;var T_TC=1;var T_TU=2;var T_TDC=3;var T_TW=4;var T_M=800;if(ble_check()){var stage=rread(R0);var mx=rand(-50,50);switch(stage){default:print("INIT");rwrite(R0,T_TC);delay(100);break;case T_TC:print("TO CENTER");rwrite(R0,T_TDC);mouse_move_to(-1e4,-1e4);delay(100);mouse_move_to(200,T_M,10,100);delay(100);break;case T_TU:print("TO UP");rwrite(R0,T_TDC);mouse_down(1);delay(10);mouse_move_to(-mx,-T_M,10,50);delay(10);mouse_up(1);delay(10);mouse_move_to(mx,T_M,10,50);delay(10);break;case T_TDC:print("DCLICK");var my=rand(0,300);rwrite(R0,T_TW);mouse_move_to(-mx,-my,4,15);delay(10);mouse_click(1);delay(100);mouse_click(1);delay(100);mouse_move_to(mx,my,4,15);delay(10);break;case T_TW:print("WAIT");rwrite(R0,T_TU);delay(rand(1e3,1e4));if(rread(R1)>20){rclear()}else{rwrite(R1,rread(R1)+1)}break}}function rand(e,r){return parseInt(Math.floor(Math.random()*(r-e))+e)}